#!/usr/bin/env sh

# Synopsis:
# Test the test runner by running it against a predefined set of solutions 
# with an expected output.

# Output:
# Outputs the diff of the expected test results against the actual test results
# generated by the test runner.

# Example:
# ./bin/run-tests.sh

exit_code=0
echo "current directory ${PWD}"

# Iterate over all test directories
for test_dir in tests/*; do
    test_dir_name=$(basename "${test_dir}")
    test_dir_path=$(realpath "${test_dir}")
    test_out_path="/tmp/${test_dir_name}"
    # Let Dyalog create the files to avoid its stdout (⎕←) length limit
    bin/run.sh "runner-tests/$test_dir_name" "${test_dir_path}" "${test_out_path}"

    file="results.json"
    expected_file="expected_${file}"

    echo "${test_dir_name}: comparing ${file} to ${expected_file}"

    if ! diff "${test_out_path}/${file}" "${test_dir_path}/${expected_file}"; then
        exit_code=1
    fi
done

exit ${exit_code}
